import { Reducer } from 'react';
import { UserType, ActionType } from '../types';

export function userRedusers(state = {} , action: ActionType) {
    switch (action.type) {
        case "ADD_USER":
            const newUser = {
                id: action.id,
                name: action.name,
                surname: action.surname,
                e_mail: action.e_mail
            }
            return newUser
        default:
            return state;
    }
}

export function usersReducers(state: UserType[], action: ActionType) {

    switch (action.type) {
        case "ADD_USER":
            return [...state, userRedusers({}, action)]
        case "REMOVE_USER":
            return state.filter(
                s => s.id !== action.id
            )
        default:
            return state;
    }
}
