import React, { useState } from 'react';
import styles from './index.module.css'
import TabsNav from './tabsnav';
import Tab from './tab';


interface MySkill {
    focusOfSkill: String;
    nameOfSkill: String;
}

const skills = [
  {
    focusOfSkill: 'Frontend',
    nameOfSkill: 'HTML'
  },
  {
    focusOfSkill: 'Frontend',
    nameOfSkill: 'CSS'
  },
  {
    focusOfSkill: 'Frontend',
    nameOfSkill: 'JS'
  },
  {
    focusOfSkill: 'Backend',
    nameOfSkill: 'Python'
  },
  {
    focusOfSkill: 'Backend',
    nameOfSkill: 'NodeJS'
  },

]

const MySkills = (skills: MySkill[]) => {

  const [value, setValue] = useState('')
  const handleChange = (value: any) => {
    setValue(value);
  }

  let changedSkills = [];

  for(let i = 0; i < skills.length; i++) {
    if(value === skills[i].focusOfSkill) {
      changedSkills.push(skills[i].nameOfSkill);
    }
  }

  return (
    <section id='mySkills' className={styles.main_mySkills}>
        <TabsNav onChange = {handleChange} />
        <Tab skills={changedSkills} nameSkill={value}/>
    </section>
  )
}

export default MySkills
