import React from 'react'
import styles from './index.module.css'



interface MyBioProps {
    bioData: {
        city: string;
        birthday: Date;
        nameCollege: string;
        yearStop: string;
        courses: {
            nameSchool: string;
            profession: string | null;
            stack: string[] | null;
            start: Date;
            stop: Date | null;
        }[]
    }
}

const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec', ]

const getFullDate = (date: Date, conditionOfTime: Boolean) => {
    let day = date.getDate() < 9 ? `0${date.getDate}` : date.getDate()
    const year = date.getFullYear();
    let month = date.getMonth();
    let hour = date.getHours() % 12;
    hour = hour ? hour : 12
    const min = date.getMinutes() <= 9 ? `0${date.getMinutes()}` : date.getMinutes();
    const am_Or_pm = date.getHours() < 12 ? 'AM' : 'PM'

    if(conditionOfTime === false) return `${day} ${months[month]} ${year}`;
    if(conditionOfTime === true) return `${day} ${months[month]} ${year} ${hour}.${min} ${am_Or_pm}`;
}

const Biography = (props: MyBioProps) => {

    const arrCourses: Array<React.ReactElement> = []

    // eslint-disable-next-line array-callback-return
    props.bioData.courses.map(course => {
        arrCourses.push(
            <div className={styles.course_block}>
                <p className={styles.course_block_title}>
                    {course.nameSchool}
                </p>
                <p className={styles.course_title}>
                    {course.profession? 'PROFESSION:' : null}
                </p>
                <p className={styles.bio_block_descr}>
                    {course.profession}
                </p>
                <p className={styles.course_block_title}>
                    {course.stack? 'STACK:' : null}
                </p>
                <p className={styles.bio_block_descr}>
                    {course.stack}
                </p>
                <p className={styles.course_block_title}>
                    DURATION OF TRAINING:
                </p>
                <p className={styles.bio_block_descr}>
                    {`${months[course.start.getMonth()]} ${course.start.getFullYear()} - ${course.stop? months[course.stop.getMonth()] : 'p.'} ${course.stop ? course.stop.getFullYear() : 'd.'}`}
                </p>
            </div>
        )
    })

  return (
    <div>
        <h2 className={styles.bio_title}>
            MY BIOGRAPHY
        </h2>
        <div>
            <h3 className={styles.bio_block_title}>
                CITY
            </h3>
            <p className={styles.bio_block_descr}>
                {props.bioData.city}
            </p>
        </div>
        <div>
            <h3 className={styles.bio_block_title}>
                BIRTHDAY
            </h3>
            <p className={styles.bio_block_descr}>
            {getFullDate(props.bioData.birthday, false)}
            </p>
        </div>
        <div>
            <h3 className={styles.bio_block_title}>
                EDUCATION
            </h3>
            <p className={styles.bio_block_descr}>
                {props.bioData.nameCollege}
            </p>
            <p className={styles.bio_block_descr}>
                {props.bioData.yearStop}
            </p>
        </div>
        <div>
            <h3 className={styles.bio_block_title}>
                COURSES & TRAININGS
            </h3>
            {arrCourses}
        </div>


    </div>
  )
}

export default Biography
