import React from 'react'
import styles from './index.module.css'
import getFullDate from '../../../../utils/getFullDate';

interface MyPostProps {
    descr:String;
    mediaLink: String | null;
    createdAt: Date; 
    handleLike: Function;
    likes: number;
}
const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec', ]

const Post = (props: MyPostProps) => {
    const img = props.mediaLink? <img className={styles.post_img} src={`${props.mediaLink}`} alt="Post's media file" /> : null;
  return (
    <div className={styles.post}>
        <time className={styles.post_time} dateTime={`${props.createdAt}`}>
            {getFullDate(props.createdAt, true, months)}
        </time>
        <div className={styles.post_content}>
            <p className={styles.post_descr}>
                {props.descr}
            </p>
                {img}
        </div>
        <button className={styles.post_like}>
        <svg width="34" height="30" viewBox="0 0 34 30" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M17 7.25712C13.6667 -0.566934 2 0.2664 2 10.2664C2 20.2664 17 28.6001 17 28.6001C17 28.6001 32 20.2664 32 10.2664C32 0.2664 20.3333 -0.566934 17 7.25712Z" stroke="white" stroke-width="2.33" stroke-linecap="round" stroke-linejoin="round"/>
        </svg>

        </button>
    </div>
  )
}

export default Post
