import styles from './Form.module.css'
import { store } from '../../app/store'
import { UserType } from '../../types';
import { FocusEventHandler, useRef, useState } from 'react';
const { v4: uuidv4 } = require('uuid');

const Form = () => {
    const _name = useRef<HTMLInputElement>(null)
    const _surname = useRef<HTMLInputElement>(null)
    const _email = useRef<HTMLInputElement>(null)

    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [email, setEmail] = useState('');
    const [nameDirty, setNameDirty] = useState(false);
    const [surnameDirty, setSurnameDirty] = useState(false);
    const [emailDirty, setEmailDirty] = useState(false);
    const [nameError, setNameError] = useState("This field is required. Please input user's name.");
    const [surnameError, setSurnameError] = useState("This field is required. Please input user's surname.");
    const [emailError, setEmailError] = useState("This field is required. Please input user's email.");

    const resetForm = () => {
        setName(name);
        setSurname(surname);
        setEmail(email)
    }

    const emailValidateHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
        setEmail(e.currentTarget.value)
        const regexp = /^[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?$/ ;
        if(!regexp.test(String(e.currentTarget.value).toLowerCase())) {
            setEmailError("Email isn't valid")
        }
    }

    const handlerBlur = (e: React.FocusEvent<HTMLInputElement>) => {
        switch (e.currentTarget.name) {
            case 'name':
                setNameDirty(true)
                break;
            case 'surname':
                setSurnameDirty(true)
                break;  
            case 'email':
                setEmailDirty(true)
                break;


            default:
                break;
        }
    } 

    const handleSubmit = (e:React.FormEvent) => {
        e.preventDefault();
        try {
            if(_name.current && _surname.current && _email.current) {
                const userData: Omit<UserType, 'type'> = {
                    id: uuidv4(),
                    name: _name.current.value,
                    surname: _surname.current.value,
                    e_mail: _email.current.value,
                }
                    store.dispatch({type: 'ADD_USER', payload:userData})
                    console.log(store.getState())
                    resetForm()
            } else {

            }
        } catch (error) {
            console.log(error)
        }
    }
  return (
    <div>
        <form onSubmit={handleSubmit}>
            {(nameDirty && nameError) && <div style={{color: 'red'}}>{nameError}</div>}
            <input onBlur={e => handlerBlur(e)} name='name' ref={_name} type="text" placeholder='Name' value={name}/>
            {(surnameDirty && surnameError) && <div style={{color: 'red'}}>{surnameError}</div>}
            <input onBlur={e => handlerBlur(e)} name='surname' ref={_surname} type="text" placeholder='Surname' value={surname}/>
            {(emailDirty && emailError) && <div style={{color: 'red'}}>{emailError}</div>}
            <input style={{color: 'black'}} onChange={e => emailValidateHandler(e)} onBlur={e => handlerBlur(e)} name='email' ref={_email} type="email" placeholder='E-Mail' value={email}/>
            <button>Submit</button>
        </form>
    </div>
  )
}

export default Form
