import styles from './UserList.module.css'
import { store } from '../../app/store'
import { UserType } from '../../types';
import { ButtonHTMLAttributes, useState } from 'react'

// interface UserListProps {
//   users: UserType[]
// }


const UserList = (/*{users}: UserListProps*/) => {
  // const [users, setUsers] = useState()
  const users: UserType[] = store.getState().usersReducers
  // console.log(users)


  const usersTableRows: JSX.Element[] = []

  users.map(user => {
    const deleteHandler = (e: React.MouseEvent) => {
      store.dispatch({type:'REMOVE_USER', payload: {id: `${user.id}`, name: `${user.name}`, surname: `${user.surname}`, e_mail: `${user.e_mail}`}})
      window.location.reload()
    }
    usersTableRows.push(
      <tr key={user.id}>
        <td> {user.name} </td>
        <td> {user.surname} </td>
        <td> {user.e_mail} </td>
        <td>
          <button onClick={e => deleteHandler(e)}>
            Delete
          </button>
        </td>
      </tr>
    )
  })


  return (
    <div> 
        <table>
      <thead>
        <td>Name</td>
        <td>Surame</td>
        <td>Email</td>
      </thead>
      <tbody>
          {usersTableRows}
      </tbody>
        </table>
    </div>
  )
}

export default UserList
