import { store } from '../../app/store'
import { UserType } from '../../types';
import { useRef, useState } from 'react';
import { validateHandler } from '../utils/validateHandler';
const { v4: uuidv4 } = require('uuid');
// eslint-disable-next-line import/first
import styles from './Form.module.css';

interface FormSubmit {
    update: Function;
}

const Form = ({update}: FormSubmit) => {
    const _name = useRef<HTMLInputElement>(null)
    const _surname = useRef<HTMLInputElement>(null)
    const _email = useRef<HTMLInputElement>(null)

    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [email, setEmail] = useState('');

    const [nameDirty, setNameDirty] = useState(false);
    const [surnameDirty, setSurnameDirty] = useState(false);
    const [emailDirty, setEmailDirty] = useState(false);

    const [nameError, setNameError] = useState("This field is required. Please input user's name.");
    const [surnameError, setSurnameError] = useState("This field is required. Please input user's surname.");
    const [emailError, setEmailError] = useState("This field is required. Please input user's email.");

    const resetForm = () => {
        setName('');
        setSurname('');
        setEmail('')
    }

    // const validateHandler =(e: React.ChangeEvent<HTMLInputElement>) => {
    //     const regexpNameOrSurname = /^[a-zA-Zа-яА-я]+$/;
    //     const regexpEmail = /^[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?$/ ;
    //     switch (e.target.name) {
    //         case 'name':
    //             setName(e.target.value)
    //             if(!regexpNameOrSurname.test(String(e.currentTarget.value).toLowerCase())) {
    //                 setNameError("Name isn't valid")
    //             } else {
    //                 setNameError("")
    //             }
    //             break;
    //         case 'surname':
    //             setSurname(e.target.value)
    //             if(!regexpNameOrSurname.test(String(e.currentTarget.value).toLowerCase())) {
    //                 setSurnameError("Surname isn't valid")
    //             } else {
    //                 setSurnameError("")
    //             }
    //             break;
    //         case 'email':
    //             setEmail(e.target.value)
    //             if(!regexpEmail.test(String(e.currentTarget.value).toLowerCase())) {
    //                 setEmailError("Email isn't valid")
    //             } else {
    //                 setEmailError("")
    //             }
    //             break;
    //     }
    // }


    const handlerBlur = (e: React.FocusEvent<HTMLInputElement>) => {
        switch (e.currentTarget.name) {
            case 'name':
                setNameDirty(true)
                break;
            case 'surname':
                setSurnameDirty(true)
                break;  
            case 'email':
                setEmailDirty(true)
                break;
            default:
                break;
        }
    } 

    const handleSubmit = (e:React.FormEvent) => {
        e.preventDefault();
        try {
            if(name && surname && email) {
                const userData: Omit<UserType, 'type'> = {
                    id: uuidv4(),
                    name: _name.current? _name.current.value : '' ,
                    surname: _surname.current?_surname.current.value : '',
                    e_mail: _email.current?_email.current.value : '',
                }
                    store.dispatch({type: 'ADD_USER', payload:userData})
                    console.log(store.getState())
                    resetForm()
            }
            update()
        } catch (error) {
            console.log(error)

        }
    }
  return (
    <div>
        <form className={styles.form} onSubmit={handleSubmit}>
            {(nameDirty && nameError) && <div style={{color: 'red'}}>{nameError}</div>}
            <input 
            className={styles.input}
            onBlur={e => handlerBlur(e)} 
            onChange={e => validateHandler(e, setName, setNameError)}
            name='name' 
            ref={_name} 
            type="text" 
            placeholder='Name' 
            value={name}/>
            {(surnameDirty && surnameError) && <div style={{color: 'red'}}>{surnameError}</div>}
            <input 
            className={styles.input}
            onChange={e => validateHandler(e, setSurname, setSurnameError)}
            onBlur={e => handlerBlur(e)} 
            name='surname' 
            ref={_surname} 
            type="text" 
            placeholder='Surname' 
            value={surname}/>
            {(emailDirty && emailError) && <div style={{color: 'red'}}>{emailError}</div>}
            <input 
            className={styles.input}
            onChange={e => validateHandler(e, setEmail, setEmailError)} 
            onBlur={e => handlerBlur(e)} 
            name='email' 
            ref={_email} 
            type="email" 
            placeholder='E-Mail' 
            value={email}/>
            <button className={styles.button}>Submit</button>
        </form>
    </div>
  )
}
export default Form
