import React, { JSXElementConstructor, ReactElement } from 'react'
import styles from './index.module.css'
import { JsxElement } from 'typescript';



interface TabsNavProps {
    onChange: Function;
    focusesAndTypesOfSkillsArr: Object[];
}

const TabsNav = (props: TabsNavProps) => {
    let TabsNavItemsHard: Array<ReactElement>= [];
    let TabsNavItemsSoft = [];

    const focusesAndTypesOfSkillsArr = props.focusesAndTypesOfSkillsArr


    const handleChange = (event: any) => {
        props.onChange(event.target.value);
        TabsNavItemsHard.forEach((el) => {
            const newClassName = el.props.children.props.className.replace(`${styles.button_tab__active}`, '')
            el.props.children.props.className = newClassName
        })
        event.target.className += ` ${styles.button_tab__active}`
        console.log(event.target.className);
    }

    for(let i = 0; i < focusesAndTypesOfSkillsArr.length; i++) {
        if(Object.values(focusesAndTypesOfSkillsArr[i])[0] === 'Hard') {
        TabsNavItemsHard.push(
                <li className={`${Object.values(focusesAndTypesOfSkillsArr[i])[0]}`}>
                    <button onClick={handleChange} className={`${styles.button_tag}`} value={`${Object.values(focusesAndTypesOfSkillsArr[i])[1]}`}>
                    {Object.values(focusesAndTypesOfSkillsArr[i])[1]}
                    </button>
                </li>
                )
        } else {
            TabsNavItemsSoft.push(
                <li className={`${Object.values(focusesAndTypesOfSkillsArr[i])[0]}`}>
                    <button onClick={handleChange} className={`${styles.button_tag}`} value={`${Object.values(focusesAndTypesOfSkillsArr[i])[1]}`}>
                    {Object.values(focusesAndTypesOfSkillsArr[i])[1]}
                    </button>
                </li>
                )
        }
    }

  return (
    <div className={styles.main_menu}>
        <h2 className={styles.mySkills_title}>My skills</h2>
        <div>
            <h4 className={styles.skill_type}>
                HARD
            </h4>
            <ul className={styles.tag_list}>
                {TabsNavItemsHard}
            </ul>
        </div>
        <div>
            <h4 className={styles.skill_type}>
                SOFT
            </h4>
            <ul className={styles.tag_list}>
                <li>
                    {TabsNavItemsSoft}
                </li>
            </ul>
        </div>
    </div>
  )
}

export default TabsNav
