import React from 'react'
import styles from './index.module.css'



interface MyBioProps {
    bioData: {
        city: string;
        birthday: Date;
        nameCollege: string;
        yearStop: string;
        courses: {
            nameSchool: string;
            profession: string | null;
            stack: string[] | null;
            start: Date;
            stop: Date;
        }[]
    }
}

const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec', ]

const getFullDate = (date: Date, conditionOfTime: Boolean) => {
    let day = date.getDate() < 9 ? `0${date.getDate}` : date.getDate()
    const year = date.getFullYear();
    let month = date.getMonth();
    let hour = date.getHours() % 12;
    hour = hour ? hour : 12
    const min = date.getMinutes() <= 9 ? `0${date.getMinutes()}` : date.getMinutes();
    const am_Or_pm = date.getHours() < 12 ? 'AM' : 'PM'

    if(conditionOfTime === false) return `${day} ${month} ${year}`;
    if(conditionOfTime === true) return `${day} ${months[month]} ${year} ${hour}.${min} ${am_Or_pm}`;
}

const Biography = (props: MyBioProps) => {
  return (
    <div>
        <h2 className={styles.bio_title}>
            MY BIOGRAPHY
        </h2>
        <div>
            <h3>
                CITY
            </h3>
            <div>
                {props.bioData.city}
            </div>
        </div>
        <div>
            <h3>
                BIRTHDAY
            </h3>
            <div>
            {/* {getFullDate(props.bioData.birthday, true)} */}
            {getFullDate(new Date(), true)}
            </div>
        </div>
        <div>
            <h3>
                EDUCATION
            </h3>
            <div>

            </div>
        </div>
        <div>
            <h3>
                COURSES & TRAININGS
            </h3>
            <div>

            </div>
        </div>


    </div>
  )
}

export default Biography
