import React from 'react'
import styles from './index.module.css'



interface MyBioProps {
    bioData: {
        city: string;
        birthday: Date;
        nameCollege: string;
        yearStop: string;
        courses: {
            nameSchool: string;
            profession: string | null;
            stack: string[] | null;
            start: Date;
            stop: Date;
        }[]
    }
}

const getFullDate = (date: Date, conditionOfTime: Boolean) => {
    const day = date.getDate();
    const year = date.getFullYear();
    let month;
    const am_Or_pm = date.getHours() < 12 ? 'AM' : 'PM'
    const hour = date.getHours() <= 9 ? `0${date.getHours()}` : date.getHours();
    const min = date.getMinutes() <= 9 ? `0${date.getMinutes()}` : date.getMinutes();
    switch (date.getMonth()) {
        case 0 : 
            month = 'Jan'
            break;
        case 1 : 
            month = 'Feb'
            break;
        case 2 : 
            month = 'Mar'
            break;
        case 3 : 
            month = 'Apr'
            break;
        case 4 : 
            month = 'May'
            break;
        case 5 : 
            month = 'Jun'
            break;
        case 6 : 
            month = 'Jul'
            break;
        case 7 : 
            month = 'Aug'
            break;
        case 8 : 
            month = 'Sep'
            break;
        case 9 : 
            month = 'Oct'
            break;
        case 10 : 
            month = 'Nov'
            break;
        case 11 : 
            month = 'Dec'
            break;
    }

    if(conditionOfTime === false) return `${day} ${month} ${year}`;
    if(conditionOfTime === true) return `${day} ${month} ${year} ${hour}.${min} ${am_Or_pm}`;
}

const Biography = (props: MyBioProps) => {
  return (
    <div>
        <h2 className={styles.bio_title}>
            MY BIOGRAPHY
        </h2>
        <div>
            <h3>
                CITY
            </h3>
            <div>
                {props.bioData.city}
            </div>
        </div>
        <div>
            <h3>
                BIRTHDAY
            </h3>
            <div>
            {/* {getFullDate(props.bioData.birthday, true)} */}
            {getFullDate(new Date(), true)}
            </div>
        </div>
        <div>
            <h3>
                EDUCATION
            </h3>
            <div>

            </div>
        </div>
        <div>
            <h3>
                COURSES & TRAININGS
            </h3>
            <div>

            </div>
        </div>


    </div>
  )
}

export default Biography
