import { Reducer } from 'react';
import { UserType, ActionType } from '../types';

export function userRedusers(state: UserType | undefined, action: ActionType){
    switch (action.type) {
        case "ADD_USER":
            const newUser = {
                id: action.payload.id,
                name: action.payload.name,
                surname: action.payload.surname,
                e_mail: action.payload.e_mail
            }
            return newUser
        default:
            return state;
    }
}

export function usersReducers(state: UserType[], action: ActionType) {

    switch (action.type) {
        case "ADD_USER":
            return [...state, userRedusers({}, action)]
        case "REMOVE_USER":
            return state.filter(
                s => s.id !== action.payload.id
            )
        default:
            return state;
    }
}
